package ecust.system.core;import ecust.system.core.manager.ProcessSchedulingManager;import org.springframework.boot.ApplicationArguments;import org.springframework.boot.ApplicationRunner;import org.springframework.stereotype.Component;@Componentpublic class Initializer implements ApplicationRunner {	private ProcessSchedulingManager manager = ProcessSchedulingManager.getProcessSchedulingManager();	@Override	public void run(ApplicationArguments args) throws Exception {		manager.start();	}}